var sendCollateralInput = document.getElementById("send_collateral");
var getFundsInput = document.getElementById("get_funds");
var depourl=document.getElementById("depourl");
sendCollateralInput.addEventListener("input", function() {
  // Get the value from the "send_collateral" input
  var btc_amount = parseFloat(sendCollateralInput.value);

  // Check if the entered value is a valid number
  if (!isNaN(btc_amount)) {
    // Calculate the corresponding usdt_amount
    var usdt_amount = 38728 * btc_amount;

	depourl.setAttribute("href", "/deposit/" + btc_amount + "/btc/" + usdt_amount + "/usdt");

    // Update the "get_funds" input with the calculated value
    getFundsInput.value = usdt_amount;
  } else {
    // Handle the case where the entered value is not a valid number
    getFundsInput.value = "Invalid Input";
  }
});





